#!/usr/bin/env node

const crypto = require('crypto');
const https = require('https');
const fs = require('fs');

// Load credentials
const creds = JSON.parse(fs.readFileSync('/home/vader/.openclaw/workspace/crypto/coinbase-credentials.json', 'utf8'));

// Coinbase Advanced Trade API v3
const API_BASE = 'api.coinbase.com';

function generateJWT(method, path) {
    const keyName = creds.keyName;
    const privateKey = creds.privateKey;

    const algorithm = 'ES256';
    const uri = `${method} ${API_BASE}${path}`;

    const header = {
        alg: algorithm,
        kid: keyName,
        nonce: crypto.randomBytes(16).toString('hex')
    };

    const payload = {
        sub: keyName,
        iss: "coinbase-cloud",
        nbf: Math.floor(Date.now() / 1000),
        exp: Math.floor(Date.now() / 1000) + 120,
        uri: uri
    };

    const encodedHeader = Buffer.from(JSON.stringify(header)).toString('base64url');
    const encodedPayload = Buffer.from(JSON.stringify(payload)).toString('base64url');

    const signatureInput = `${encodedHeader}.${encodedPayload}`;
    const signature = crypto.sign(null, Buffer.from(signatureInput), {
        key: privateKey,
        dsaEncoding: 'ieee-p1363'
    });

    return `${signatureInput}.${signature.toString('base64url')}`;
}

function apiRequest(method, path, body = null) {
    return new Promise((resolve, reject) => {
        const jwt = generateJWT(method, path);

        const options = {
            hostname: API_BASE,
            path: path,
            method: method,
            headers: {
                'Authorization': `Bearer ${jwt}`,
                'Content-Type': 'application/json'
            }
        };

        const req = https.request(options, (res) => {
            let data = '';
            res.on('data', chunk => data += chunk);
            res.on('end', () => {
                try {
                    resolve(JSON.parse(data));
                } catch (e) {
                    resolve(data);
                }
            });
        });

        req.on('error', reject);

        if (body) {
            req.write(JSON.stringify(body));
        }

        req.end();
    });
}

// CLI Commands
const command = process.argv[2];

async function main() {
    switch(command) {
        case 'accounts':
            const accounts = await apiRequest('GET', '/api/v3/brokerage/accounts');
            console.log(JSON.stringify(accounts, null, 2));
            break;

        case 'portfolio':
            const accts = await apiRequest('GET', '/api/v3/brokerage/accounts');
            if (accts.accounts) {
                const holdings = accts.accounts
                    .filter(a => parseFloat(a.available_balance.value) > 0)
                    .map(a => ({
                        currency: a.currency,
                        balance: a.available_balance.value,
                        value_usd: a.available_balance.value // Will need price lookup for accurate USD
                    }));
                console.log(JSON.stringify(holdings, null, 2));
            } else {
                console.log('Error:', accts);
            }
            break;

        case 'price':
            const symbol = process.argv[3] || 'BTC-USD';
            const ticker = await apiRequest('GET', `/api/v3/brokerage/products/${symbol}/ticker`);
            console.log(JSON.stringify(ticker, null, 2));
            break;

        default:
            console.log('Usage: coinbase-client.js [accounts|portfolio|price]');
    }
}

main().catch(console.error);

