#!/usr/bin/env node

const { apiRequest } = require('./lib/coinbase-api.js');
const fs = require('fs');

const ALERTS_FILE = '/home/vader/.openclaw/workspace/crypto/data/alerts.json';
const HISTORY_FILE = '/home/vader/.openclaw/workspace/crypto/data/alert-history.json';

// Ensure data directory exists
const dataDir = '/home/vader/.openclaw/workspace/crypto/data';
if (!fs.existsSync(dataDir)) {
    fs.mkdirSync(dataDir, { recursive: true });
}

// Load alerts
function loadAlerts() {
    if (!fs.existsSync(ALERTS_FILE)) {
        return [];
    }
    return JSON.parse(fs.readFileSync(ALERTS_FILE, 'utf8'));
}

// Save alerts
function saveAlerts(alerts) {
    fs.writeFileSync(ALERTS_FILE, JSON.stringify(alerts, null, 2));
}

// Log alert trigger
function logAlert(alert, price) {
    let history = [];
    if (fs.existsSync(HISTORY_FILE)) {
        history = JSON.parse(fs.readFileSync(HISTORY_FILE, 'utf8'));
    }

    history.push({
        timestamp: new Date().toISOString(),
        symbol: alert.symbol,
        condition: alert.condition,
        target: alert.target,
        actual: price,
        message: alert.message
    });

    fs.writeFileSync(HISTORY_FILE, JSON.stringify(history, null, 2));
}

// Add alert
async function addAlert(symbol, condition, target, message) {
    const alerts = loadAlerts();

    const newAlert = {
        id: Date.now().toString(),
        symbol: symbol.toUpperCase(),
        condition: condition, // 'above' or 'below'
        target: parseFloat(target),
        message: message || `${symbol} ${condition} ${target}`,
        created: new Date().toISOString(),
        active: true
    };

    alerts.push(newAlert);
    saveAlerts(alerts);

    console.log(`âœ… Alert added: ${newAlert.symbol} ${newAlert.condition} $${newAlert.target}`);
    console.log(`Alert ID: ${newAlert.id}`);
    return newAlert;
}

// List alerts
function listAlerts() {
    const alerts = loadAlerts();

    if (alerts.length === 0) {
        console.log('No active alerts.');
        return;
    }

    console.log('\nðŸ“¢ Active Price Alerts:\n');
    alerts.forEach(alert => {
        if (alert.active) {
            console.log(`[${alert.id}] ${alert.symbol} ${alert.condition} $${alert.target}`);
            console.log(`    Message: ${alert.message}`);
            console.log(`    Created: ${alert.created}\n`);
        }
    });
}

// Remove alert
function removeAlert(id) {
    let alerts = loadAlerts();
    const before = alerts.length;
    alerts = alerts.filter(a => a.id !== id);

    if (alerts.length < before) {
        saveAlerts(alerts);
        console.log(`âœ… Alert ${id} removed.`);
    } else {
        console.log(`âŒ Alert ${id} not found.`);
    }
}

// Check alerts (run periodically)
async function checkAlerts() {
    const alerts = loadAlerts();
    const activeAlerts = alerts.filter(a => a.active);

    if (activeAlerts.length === 0) {
        return;
    }

    // Get unique symbols
    const symbols = [...new Set(activeAlerts.map(a => a.symbol))];

    for (const symbol of symbols) {
        try {
            const ticker = await apiRequest('GET', `/api/v3/brokerage/products/${symbol}-USD/ticker`);

            if (!ticker.trades || ticker.trades.length === 0) {
                continue;
            }

            const currentPrice = parseFloat(ticker.trades[0].price);

            // Check each alert for this symbol
            activeAlerts
                .filter(a => a.symbol === symbol)
                .forEach(alert => {
                    let triggered = false;

                    if (alert.condition === 'above' && currentPrice >= alert.target) {
                        triggered = true;
                    } else if (alert.condition === 'below' && currentPrice <= alert.target) {
                        triggered = true;
                    }

                    if (triggered) {
                        console.log(`\nðŸš¨ ALERT TRIGGERED! ðŸš¨`);
                        console.log(`${alert.symbol}: $${currentPrice}`);
                        console.log(`Condition: ${alert.condition} $${alert.target}`);
                        console.log(`Message: ${alert.message}\n`);

                        // Log and deactivate
                        logAlert(alert, currentPrice);
                        alert.active = false;
                    }
                });

        } catch (err) {
            console.error(`Error checking ${symbol}:`, err.message);
        }
    }

    // Save updated alerts (deactivated ones)
    saveAlerts(alerts);
}

// Show alert history
function showHistory() {
    if (!fs.existsSync(HISTORY_FILE)) {
        console.log('No alert history.');
        return;
    }

    const history = JSON.parse(fs.readFileSync(HISTORY_FILE, 'utf8'));

    console.log('\nðŸ“œ Alert History:\n');
    history.slice(-10).forEach(h => {
        console.log(`[${h.timestamp}] ${h.symbol}: $${h.actual}`);
        console.log(`   Triggered: ${h.condition} $${h.target}`);
        console.log(`   ${h.message}\n`);
    });
}

// CLI
const command = process.argv[2];

async function main() {
    switch(command) {
        case 'add':
            const symbol = process.argv[3];
            const condition = process.argv[4]; // 'above' or 'below'
            const target = process.argv[5];
            const message = process.argv.slice(6).join(' ');

            if (!symbol || !condition || !target) {
                console.log('Usage: price-alerts.js add <SYMBOL> <above|below> <PRICE> [message]');
                console.log('Example: price-alerts.js add BTC above 100000 "Bitcoin hit 100K!"');
                return;
            }

            await addAlert(symbol, condition, target, message);
            break;

        case 'list':
            listAlerts();
            break;

        case 'remove':
            const id = process.argv[3];
            if (!id) {
                console.log('Usage: price-alerts.js remove <ALERT_ID>');
                return;
            }
            removeAlert(id);
            break;

        case 'check':
            await checkAlerts();
            break;

        case 'history':
            showHistory();
            break;

        default:
            console.log('Coinbase Price Alerts');
            console.log('');
            console.log('Commands:');
            console.log('  add <SYMBOL> <above|below> <PRICE> [message]  - Add price alert');
            console.log('  list                                           - List active alerts');
            console.log('  remove <ID>                                    - Remove alert');
            console.log('  check                                          - Check all alerts now');
