#!/usr/bin/env node

const { apiRequest } = require('./lib/coinbase-api.js');
const fs = require('fs');

const DCA_FILE = '/home/vader/.openclaw/workspace/crypto/data/dca-config.json';
const TRADE_LOG = '/home/vader/.openclaw/workspace/crypto/data/trade-log.json';

// Ensure data directory exists
const dataDir = '/home/vader/.openclaw/workspace/crypto/data';
if (!fs.existsSync(dataDir)) {
    fs.mkdirSync(dataDir, { recursive: true });
}

// Load DCA config
function loadDCA() {
    if (!fs.existsSync(DCA_FILE)) {
        return [];
    }
    return JSON.parse(fs.readFileSync(DCA_FILE, 'utf8'));
}

// Save DCA config
function saveDCA(configs) {
    fs.writeFileSync(DCA_FILE, JSON.stringify(configs, null, 2));
}

// Log trade
function logTrade(trade) {
    let log = [];
    if (fs.existsSync(TRADE_LOG)) {
        log = JSON.parse(fs.readFileSync(TRADE_LOG, 'utf8'));
    }

    log.push({
        ...trade,
        timestamp: new Date().toISOString()
    });

    fs.writeFileSync(TRADE_LOG, JSON.stringify(log, null, 2));
}

// Add DCA strategy
function addDCA(symbol, amountUSD, intervalHours, maxBuys) {
    const configs = loadDCA();

    const newDCA = {
        id: Date.now().toString(),
        symbol: symbol.toUpperCase(),
        amountUSD: parseFloat(amountUSD),
        intervalHours: parseInt(intervalHours),
        maxBuys: parseInt(maxBuys) || null, // null = unlimited
        buysExecuted: 0,
        nextBuy: new Date(Date.now() + intervalHours * 3600000).toISOString(),
        active: true,
        created: new Date().toISOString()
    };

    configs.push(newDCA);
    saveDCA(configs);

    console.log(`âœ… DCA Strategy Added:`);
    console.log(`   Symbol: ${newDCA.symbol}`);
    console.log(`   Amount: $${newDCA.amountUSD} USD per buy`);
    console.log(`   Interval: Every ${newDCA.intervalHours} hours`);
    console.log(`   Max Buys: ${newDCA.maxBuys || 'Unlimited'}`);
    console.log(`   Next Buy: ${newDCA.nextBuy}`);
    console.log(`   Strategy ID: ${newDCA.id}`);
}

// List DCA strategies
function listDCA() {
    const configs = loadDCA();

    if (configs.length === 0) {
        console.log('No DCA strategies configured.');
        return;
    }

    console.log('\nðŸ’° Active DCA Strategies:\n');
    configs.forEach(dca => {
        if (dca.active) {
            console.log(`[${dca.id}] ${dca.symbol}`);
            console.log(`   Amount: $${dca.amountUSD} every ${dca.intervalHours}h`);
            console.log(`   Progress: ${dca.buysExecuted}/${dca.maxBuys || 'âˆž'} buys`);
            console.log(`   Next Buy: ${dca.nextBuy}`);
            console.log(`   Created: ${dca.created}\n`);
        }
    });
}

// Remove DCA strategy
function removeDCA(id) {
    let configs = loadDCA();
    const before = configs.length;
    configs = configs.filter(d => d.id !== id);

    if (configs.length < before) {
        saveDCA(configs);
        console.log(`âœ… DCA strategy ${id} removed.`);
    } else {
        console.log(`âŒ DCA strategy ${id} not found.`);
    }
}

// Execute DCA buys (run periodically)
async function executeDCA(dryRun = false) {
    const configs = loadDCA();
    const now = new Date();

    for (const dca of configs) {
        if (!dca.active) continue;

        const nextBuy = new Date(dca.nextBuy);

        if (now >= nextBuy) {
            // Check if max buys reached
            if (dca.maxBuys && dca.buysExecuted >= dca.maxBuys) {
                console.log(`â¸ï¸  ${dca.symbol} DCA completed (${dca.buysExecuted} buys)`);
                dca.active = false;
                continue;
            }

            // Get current price
            try {
                const ticker = await apiRequest('GET', `/api/v3/brokerage/products/${dca.symbol}-USD/ticker`);

                if (!ticker.trades || ticker.trades.length === 0) {
                    console.log(`âŒ No price data for ${dca.symbol}`);
                    continue;
                }

                const currentPrice = parseFloat(ticker.trades[0].price);
                const quantity = (dca.amountUSD / currentPrice).toFixed(8);

                console.log(`\nðŸ’µ DCA BUY TRIGGERED: ${dca.symbol}`);
                console.log(`   Price: $${currentPrice}`);
                console.log(`   Amount: $${dca.amountUSD} USD`);
                console.log(`   Quantity: ${quantity} ${dca.symbol}`);

                if (dryRun) {
                    console.log(`   [DRY RUN - No order placed]`);
                } else {
                    // Place market buy order
                    const order = await placeBuyOrder(dca.symbol, quantity, currentPrice);

                    if (order.success) {
                        console.log(`   âœ… Order placed successfully`);
                        console.log(`   Order ID: ${order.order_id}`);

                        // Log trade
                        logTrade({
                            type: 'DCA_BUY',
                            symbol: dca.symbol,
                            quantity: quantity,
                            price: currentPrice,
                            amountUSD: dca.amountUSD,
                            orderId: order.order_id,
                            strategyId: dca.id
                        });

                        // Update DCA config
                        dca.buysExecuted++;
                        dca.nextBuy = new Date(Date.now() + dca.intervalHours * 3600000).toISOString();
                    } else {
                        console.log(`   âŒ Order failed: ${order.error}`);
                    }
                }

            } catch (err) {
                console.error(`Error executing DCA for ${dca.symbol}:`, err.message);
            }
        }
    }

    saveDCA(configs);
}

// Place buy order (market order)
async function placeBuyOrder(symbol, quantity, currentPrice) {
    try {
        const orderConfig = {
            client_order_id: `dca_${Date.now()}`,
            product_id: `${symbol}-USD`,
            side: 'BUY',
            order_configuration: {
                market_market_ioc: {
                    quote_size: (parseFloat(quantity) * currentPrice).toFixed(2)
                }
            }
        };

        const result = await apiRequest('POST', '/api/v3/brokerage/orders', orderConfig);

        if (result.success) {
            return {
                success: true,
                order_id: result.order_id || result.success_response?.order_id
            };
        } else {
            return {
                success: false,
                error: result.error_response?.error || 'Unknown error'
            };
        }

    } catch (err) {
        return {
            success: false,
            error: err.message
        };
    }
}

// Show trade log
function showTradeLog() {
    if (!fs.existsSync(TRADE_LOG)) {
        console.log('No trades logged yet.');
        return;
    }

    const log = JSON.parse(fs.readFileSync(TRADE_LOG, 'utf8'));

    console.log('\nðŸ“Š Trade Log:\n');
    log.slice(-20).forEach(trade => {
        console.log(`[${trade.timestamp}] ${trade.type}: ${trade.symbol}`);
        console.log(`   Qty: ${trade.quantity} @ $${trade.price}`);
        console.log(`   Total: $${trade.amountUSD}`);
        console.log(`   Order ID: ${trade.orderId}\n`);
    });
}

// CLI
const command = process.argv[2];

async function main() {
    switch(command) {
        case 'add':
            const symbol = process.argv[3];
            const amount = process.argv[4];
            const interval = process.argv[5];
            const maxBuys = process.argv[6];

            if (!symbol || !amount || !interval) {
                console.log('Usage: auto-buy.js add <SYMBOL> <AMOUNT_USD> <INTERVAL_HOURS> [MAX_BUYS]');
                console.log('Example: auto-buy.js add BTC 100 24 30');
                console.log('         (Buy $100 of BTC every 24 hours, max 30 times)');
                return;
            }

            addDCA(symbol, amount, interval, maxBuys);
            break;

        case 'list':
            listDCA();
            break;

        case 'remove':
            const id = process.argv[3];
            if (!id) {
                console.log('Usage: auto-buy.js remove <STRATEGY_ID>');
                return;
            }
            removeDCA(id);
            break;

        case 'execute':
            const dryRun = process.argv[3] === '--dry-run';
            await executeDCA(dryRun);
            break;

        case 'log':
            showTradeLog();
            break;

        default:
            console.log('Coinbase Auto-Buy (DCA) Manager');
            console.log('');
            console.log('Commands:');
            console.log('  add <SYMBOL> <AMOUNT_USD> <INTERVAL_HOURS> [MAX_BUYS]  - Add DCA strategy');
            console.log('  list                                                    - List active strategies');
            console.log('  remove <ID>                                             - Remove strategy');
            console.log('  execute [--dry-run]                                     - Execute pending buys');
            console.log('  log                                                     - Show trade history');
            console.log('');
            console.log('Examples:');
            console.log('  node auto-buy.js add BTC 100 24 30    # $100/day for 30 days');
            console.log('  node auto-buy.js add ETH 50 168       # $50/week unlimited');
            console.log('  node auto-buy.js execute              # Run all pending buys');
            console.log('  node auto-buy.js execute --dry-run    # Test without placing orders');
    }
}

main().catch(console.error);

#!/usr/bin/env node

const { apiRequest } = require('./lib/coinbase-api.js');

async function getAccounts() {
    const result = await apiRequest('GET', '/api/v3/brokerage/accounts');
    return result.accounts || [];
}

async function getPrice(symbol) {
    const ticker = await apiRequest('GET', `/api/v3/brokerage/products/${symbol}-USD/ticker`);

    if (!ticker.trades || ticker.trades.length === 0) {
        return null;
    }

    return {
        symbol: symbol,
        price: parseFloat(ticker.trades[0].price),
        time: ticker.trades[0].time
    };
}

async function getPortfolioValue(accounts) {
    let totalUSD = 0;
    const holdings = [];

    for (const acct of accounts) {
        const balance = parseFloat(acct.available_balance.value);

        if (balance <= 0) continue;

        let valueUSD = 0;

        if (acct.currency === 'USD') {
            valueUSD = balance;
        } else {
            try {
                const priceData = await getPrice(acct.currency);
                if (priceData) {
                    valueUSD = balance * priceData.price;
                }
            } catch (err) {
                // Skip if price unavailable
            }
        }

        holdings.push({
            currency: acct.currency,
            balance: balance,
            valueUSD: valueUSD
        });

        totalUSD += valueUSD;
    }

    return { holdings, totalUSD };
}

function formatCurrency(value) {
    return new Intl.NumberFormat('en-US', {
        style: 'currency',
        currency: 'USD',
        minimumFractionDigits: 2,
        maximumFractionDigits: 2
    }).format(value);
}

