#!/usr/bin/env node

const { apiRequest } = require('./lib/coinbase-api.js');
const fs = require('fs');

const TRADE_LOG = '/home/vader/.openclaw/workspace/crypto/data/trade-log.json';

// Log trade
function logTrade(trade) {
    let log = [];
    if (fs.existsSync(TRADE_LOG)) {
        log = JSON.parse(fs.readFileSync(TRADE_LOG, 'utf8'));
    }

    log.push({
        ...trade,
        timestamp: new Date().toISOString()
    });

    fs.writeFileSync(TRADE_LOG, JSON.stringify(log, null, 2));
}

// Market buy
async function marketBuy(symbol, amountUSD) {
    try {
        const orderConfig = {
            client_order_id: `buy_${Date.now()}`,
            product_id: `${symbol}-USD`,
            side: 'BUY',
            order_configuration: {
                market_market_ioc: {
                    quote_size: parseFloat(amountUSD).toFixed(2)
                }
            }
        };

        const result = await apiRequest('POST', '/api/v3/brokerage/orders', orderConfig);

        if (result.success) {
            console.log(`âœ… BUY ORDER PLACED`);
            console.log(`   Symbol: ${symbol}`);
            console.log(`   Amount: $${amountUSD}`);
            console.log(`   Order ID: ${result.order_id || result.success_response?.order_id}`);

            logTrade({
                type: 'MARKET_BUY',
                symbol: symbol,
                amountUSD: amountUSD,
                orderId: result.order_id || result.success_response?.order_id
            });

            return result;
        } else {
            console.log(`âŒ ORDER FAILED`);
            console.log(`   Error: ${result.error_response?.error || 'Unknown error'}`);
            return result;
        }

    } catch (err) {
        console.error('Error placing order:', err.message);
        throw err;
    }
}

// Market sell
async function marketSell(symbol, quantity) {
    try {
        const orderConfig = {
            client_order_id: `sell_${Date.now()}`,
            product_id: `${symbol}-USD`,
            side: 'SELL',
            order_configuration: {
                market_market_ioc: {
                    base_size: parseFloat(quantity).toFixed(8)
                }
            }
        };

        const result = await apiRequest('POST', '/api/v3/brokerage/orders', orderConfig);

        if (result.success) {
            console.log(`âœ… SELL ORDER PLACED`);
            console.log(`   Symbol: ${symbol}`);
            console.log(`   Quantity: ${quantity}`);
            console.log(`   Order ID: ${result.order_id || result.success_response?.order_id}`);

            logTrade({
                type: 'MARKET_SELL',
                symbol: symbol,
                quantity: quantity,
                orderId: result.order_id || result.success_response?.order_id
            });

            return result;
        } else {
            console.log(`âŒ ORDER FAILED`);
            console.log(`   Error: ${result.error_response?.error || 'Unknown error'}`);
            return result;
        }

    } catch (err) {
        console.error('Error placing order:', err.message);
        throw err;
    }
}

// Limit buy
async function limitBuy(symbol, amountUSD, limitPrice) {
    try {
        const quantity = (parseFloat(amountUSD) / parseFloat(limitPrice)).toFixed(8);

        const orderConfig = {
            client_order_id: `limit_buy_${Date.now()}`,
            product_id: `${symbol}-USD`,
            side: 'BUY',
            order_configuration: {
                limit_limit_gtc: {
                    base_size: quantity,
                    limit_price: parseFloat(limitPrice).toFixed(2)
                }
            }
        };

        const result = await apiRequest('POST', '/api/v3/brokerage/orders', orderConfig);

        if (result.success) {
            console.log(`âœ… LIMIT BUY ORDER PLACED`);
            console.log(`   Symbol: ${symbol}`);
            console.log(`   Quantity: ${quantity}`);
            console.log(`   Limit Price: $${limitPrice}`);
            console.log(`   Order ID: ${result.order_id || result.success_response?.order_id}`);

            logTrade({
                type: 'LIMIT_BUY',
                symbol: symbol,
                quantity: quantity,
                limitPrice: limitPrice,
                orderId: result.order_id || result.success_response?.order_id
            });

            return result;
        } else {
            console.log(`âŒ ORDER FAILED`);
            console.log(`   Error: ${result.error_response?.error || 'Unknown error'}`);
            return result;
        }

    } catch (err) {
        console.error('Error placing order:', err.message);
        throw err;
    }
}

// Get order status
async function getOrderStatus(orderId) {
    try {
        const result = await apiRequest('GET', `/api/v3/brokerage/orders/historical/${orderId}`);

        if (result.order) {
            console.log(`ðŸ“‹ Order Status:`);
            console.log(`   ID: ${result.order.order_id}`);
            console.log(`   Symbol: ${result.order.product_id}`);
            console.log(`   Side: ${result.order.side}`);
            console.log(`   Status: ${result.order.status}`);
            console.log(`   Filled: ${result.order.filled_size || 0}/${result.order.order_configuration?.limit_limit_gtc?.base_size || result.order.order_configuration?.market_market_ioc?.quote_size || '?'}`);
            return result.order;
        } else {
            console.log('âŒ Order not found');
            return null;
        }
    } catch (err) {
        console.error('Error fetching order:', err.message);
        throw err;
    }
}

// CLI
const command = process.argv[2];

async function main() {
    switch(command) {
        case 'buy':
            const buySymbol = process.argv[3];
            const buyAmount = process.argv[4];

            if (!buySymbol || !buyAmount) {
                console.log('Usage: trade.js buy <SYMBOL> <AMOUNT_USD>');
                console.log('Example: trade.js buy BTC 100');
                return;
            }

            await marketBuy(buySymbol.toUpperCase(), buyAmount);
            break;

        case 'sell':
            const sellSymbol = process.argv[3];
            const sellQty = process.argv[4];

            if (!sellSymbol || !sellQty) {
                console.log('Usage: trade.js sell <SYMBOL> <QUANTITY>');
                console.log('Example: trade.js sell BTC 0.001');
                return;
            }

            await marketSell(sellSymbol.toUpperCase(), sellQty);
            break;

        case 'limit-buy':
            const limitSymbol = process.argv[3];
            const limitAmount = process.argv[4];
            const limitPrice = process.argv[5];

            if (!limitSymbol || !limitAmount || !limitPrice) {
                console.log('Usage: trade.js limit-buy <SYMBOL> <AMOUNT_USD> <LIMIT_PRICE>');
                console.log('Example: trade.js limit-buy BTC 100 95000');
                return;
            }

            await limitBuy(limitSymbol.toUpperCase(), limitAmount, limitPrice);
            break;

        case 'status':
            const orderId = process.argv[3];

            if (!orderId) {
                console.log('Usage: trade.js status <ORDER_ID>');
                return;
            }

            await getOrderStatus(orderId);
            break;

        default:
            console.log('Coinbase Trade Execution');
            console.log('');
            console.log('Commands:');
            console.log('  buy <SYMBOL> <AMOUNT_USD>                    - Market buy');
            console.log('  sell <SYMBOL> <QUANTITY>                     - Market sell');
            console.log('  limit-buy <SYMBOL> <AMOUNT_USD> <PRICE>      - Limit buy order');
            console.log('  status <ORDER_ID>                            - Check order status');
            console.log('');
            console.log('Examples:');
            console.log('  node trade.js buy BTC 100           # Buy $100 of BTC at market');
            console.log('  node trade.js sell ETH 0.5          # Sell 0.5 ETH at market');
            console.log('  node trade.js limit-buy BTC 100 95000  # Buy BTC only if price drops to $95k');
    }
}

