#!/usr/bin/env node

const { apiRequest } = require('./lib/coinbase-api.js');
const fs = require('fs');
const path = require('path');

const PAPER_PORTFOLIO_FILE = '/home/vader/.openclaw/workspace/crypto/data/paper-portfolio.json';
const PAPER_TRADES_FILE = '/home/vader/.openclaw/workspace/crypto/data/paper-trades.json';
const PAPER_CONFIG_FILE = '/home/vader/.openclaw/workspace/crypto/data/paper-config.json';

// Ensure data directory exists
const dataDir = '/home/vader/.openclaw/workspace/crypto/data';
if (!fs.existsSync(dataDir)) {
    fs.mkdirSync(dataDir, { recursive: true });
}

// Initialize paper trading portfolio
function initPaperPortfolio(startingUSD = 10000) {
    const portfolio = {
        usd: startingUSD,
        holdings: {}, // symbol -> quantity
        startingUSD: startingUSD,
        createdAt: new Date().toISOString()
    };

    fs.writeFileSync(PAPER_PORTFOLIO_FILE, JSON.stringify(portfolio, null, 2));
    console.log(`âœ… Paper portfolio initialized with $${startingUSD} USD`);
    return portfolio;
}

// Load paper portfolio
function loadPaperPortfolio() {
    if (!fs.existsSync(PAPER_PORTFOLIO_FILE)) {
        return initPaperPortfolio();
    }
    return JSON.parse(fs.readFileSync(PAPER_PORTFOLIO_FILE, 'utf8'));
}

// Save paper portfolio
function savePaperPortfolio(portfolio) {
    fs.writeFileSync(PAPER_PORTFOLIO_FILE, JSON.stringify(portfolio, null, 2));
}

// Load paper trades
function loadPaperTrades() {
    if (!fs.existsSync(PAPER_TRADES_FILE)) {
        return [];
    }
    return JSON.parse(fs.readFileSync(PAPER_TRADES_FILE, 'utf8'));
}

// Log paper trade
function logPaperTrade(trade) {
    const trades = loadPaperTrades();
    trades.push({
        ...trade,
        timestamp: new Date().toISOString()
    });
    fs.writeFileSync(PAPER_TRADES_FILE, JSON.stringify(trades, null, 2));
}

// Get current price for a symbol
async function getPrice(symbol) {
    try {
        const ticker = await apiRequest('GET', `/api/v3/brokerage/products/${symbol}-USD/ticker`);

        if (!ticker.trades || ticker.trades.length === 0) {
            throw new Error(`No price data for ${symbol}`);
        }

        return parseFloat(ticker.trades[0].price);
    } catch (err) {
        throw new Error(`Failed to get price for ${symbol}: ${err.message}`);
    }
}

// Simulate fees (Coinbase Advanced: ~0.5%)
function calculateFees(amountUSD) {
    return amountUSD * 0.005; // 0.5% fee
}

// Execute paper buy
async function paperBuy(symbol, amountUSD) {
    const portfolio = loadPaperPortfolio();

    if (portfolio.usd < amountUSD) {
        console.log(`âŒ Insufficient funds. Have: $${portfolio.usd.toFixed(2)}, Need: $${amountUSD}`);
        return null;
    }

    try {
        const price = await getPrice(symbol);
        const fees = calculateFees(amountUSD);
        const netAmount = amountUSD - fees;
        const quantity = netAmount / price;

        // Update portfolio
        portfolio.usd -= amountUSD;
        portfolio.holdings[symbol] = (portfolio.holdings[symbol] || 0) + quantity;

        savePaperPortfolio(portfolio);

        const trade = {
            type: 'BUY',
            symbol: symbol,
            amountUSD: amountUSD,
            price: price,
            quantity: quantity,
            fees: fees,
            netQuantity: quantity
        };

        logPaperTrade(trade);

        console.log(`âœ… PAPER BUY: ${symbol}`);
        console.log(`   Amount: $${amountUSD.toFixed(2)}`);
        console.log(`   Price: $${price.toFixed(2)}`);
        console.log(`   Quantity: ${quantity.toFixed(8)} ${symbol}`);
        console.log(`   Fees: $${fees.toFixed(2)}`);
        console.log(`   Remaining USD: $${portfolio.usd.toFixed(2)}`);

        return trade;

    } catch (err) {
        console.error(`âŒ Paper buy failed: ${err.message}`);
        return null;
    }
}

// Execute paper sell
async function paperSell(symbol, quantity) {
    const portfolio = loadPaperPortfolio();

    const currentHolding = portfolio.holdings[symbol] || 0;

    if (currentHolding < quantity) {
        console.log(`âŒ Insufficient ${symbol}. Have: ${currentHolding.toFixed(8)}, Need: ${quantity}`);
        return null;
    }

    try {
        const price = await getPrice(symbol);
        const grossAmount = quantity * price;
        const fees = calculateFees(grossAmount);
        const netAmount = grossAmount - fees;

        // Update portfolio
        portfolio.holdings[symbol] -= quantity;
        if (portfolio.holdings[symbol] <= 0) {
            delete portfolio.holdings[symbol];
        }
        portfolio.usd += netAmount;

        savePaperPortfolio(portfolio);

        const trade = {
            type: 'SELL',
            symbol: symbol,
            quantity: quantity,
            price: price,
            grossAmount: grossAmount,
            fees: fees,
            netAmount: netAmount
        };

        logPaperTrade(trade);

        console.log(`âœ… PAPER SELL: ${symbol}`);
        console.log(`   Quantity: ${quantity.toFixed(8)} ${symbol}`);
        console.log(`   Price: $${price.toFixed(2)}`);
        console.log(`   Gross: $${grossAmount.toFixed(2)}`);
        console.log(`   Fees: $${fees.toFixed(2)}`);
        console.log(`   Net: $${netAmount.toFixed(2)}`);
        console.log(`   USD Balance: $${portfolio.usd.toFixed(2)}`);

        return trade;

    } catch (err) {
        console.error(`âŒ Paper sell failed: ${err.message}`);
        return null;
    }
}

// Show paper portfolio
async function showPaperPortfolio() {
    const portfolio = loadPaperPortfolio();

    console.log('\nâ•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•');
    console.log('          ðŸ’° PAPER TRADING PORTFOLIO ðŸ’°');
    console.log('â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•\n');

    console.log('ðŸ’µ CASH:\n');
    console.log(`   USD: $${portfolio.usd.toFixed(2)}\n`);

    if (Object.keys(portfolio.holdings).length > 0) {
        console.log('ðŸ“Š HOLDINGS:\n');

        let totalValueUSD = portfolio.usd;

        for (const [symbol, quantity] of Object.entries(portfolio.holdings)) {
            try {
                const price = await getPrice(symbol);
                const valueUSD = quantity * price;
                totalValueUSD += valueUSD;

                console.log(`   ${symbol.padEnd(6)} ${quantity.toFixed(8).padStart(15)}`);
                console.log(`            @ $${price.toFixed(2).padStart(10)} = $${valueUSD.toFixed(2)}\n`);
            } catch (err) {
                console.log(`   ${symbol.padEnd(6)} ${quantity.toFixed(8).padStart(15)}`);
                console.log(`            (price unavailable)\n`);
            }
        }

        console.log('â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€');
        console.log(`   TOTAL VALUE: $${totalValueUSD.toFixed(2)}`);

        const pnl = totalValueUSD - portfolio.startingUSD;
        const pnlPct = (pnl / portfolio.startingUSD) * 100;

        const color = pnl >= 0 ? '\x1b[32m' : '\x1b[31m'; // Green or Red
        const reset = '\x1b[0m';

        console.log(`   P&L: ${color}$${pnl.toFixed(2)} (${pnlPct.toFixed(2)}%)${reset}`);
        console.log('â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•\n');
    } else {
        console.log('(No holdings)\n');
        console.log('â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•\n');
    }
}

// Reset paper portfolio
function resetPaperPortfolio(startingUSD = 10000) {
    if (fs.existsSync(PAPER_PORTFOLIO_FILE)) {
        fs.unlinkSync(PAPER_PORTFOLIO_FILE);
    }
    if (fs.existsSync(PAPER_TRADES_FILE)) {
        fs.unlinkSync(PAPER_TRADES_FILE);
    }

    initPaperPortfolio(startingUSD);
    console.log('âœ… Paper portfolio reset');
}

// Show trade history
function showPaperTrades() {
    const trades = loadPaperTrades();

    if (trades.length === 0) {
        console.log('No paper trades yet.');
        return;
    }

    console.log('\nðŸ“œ Paper Trade History:\n');

    trades.slice(-20).forEach(trade => {
        const type = trade.type === 'BUY' ? '\x1b[32mBUY\x1b[0m ' : '\x1b[31mSELL\x1b[0m';
        console.log(`[${trade.timestamp}] ${type} ${trade.symbol}`);

        if (trade.type === 'BUY') {
            console.log(`   Amount: $${trade.amountUSD.toFixed(2)} @ $${trade.price.toFixed(2)}`);
            console.log(`   Quantity: ${trade.quantity.toFixed(8)} (fees: $${trade.fees.toFixed(2)})\n`);
        } else {
            console.log(`   Quantity: ${trade.quantity.toFixed(8)} @ $${trade.price.toFixed(2)}`);
            console.log(`   Net: $${trade.netAmount.toFixed(2)} (fees: $${trade.fees.toFixed(2)})\n`);
        }
    });
}

// CLI
const command = process.argv[2];

async function main() {
    switch(command) {
        case 'init':
            const startingUSD = parseFloat(process.argv[3]) || 10000;
            initPaperPortfolio(startingUSD);
            break;

        case 'buy':
            const buySymbol = process.argv[3];
            const buyAmount = parseFloat(process.argv[4]);

            if (!buySymbol || !buyAmount) {
                console.log('Usage: paper-trading.js buy <SYMBOL> <AMOUNT_USD>');
                console.log('Example: paper-trading.js buy BTC 1000');
                return;
            }

            await paperBuy(buySymbol.toUpperCase(), buyAmount);
            break;

        case 'sell':
            const sellSymbol = process.argv[3];
            const sellQty = parseFloat(process.argv[4]);

            if (!sellSymbol || !sellQty) {
                console.log('Usage: paper-trading.js sell <SYMBOL> <QUANTITY>');
                console.log('Example: paper-trading.js sell BTC 0.01');
                return;
            }

            await paperSell(sellSymbol.toUpperCase(), sellQty);
            break;

        case 'portfolio':
        case 'status':
            await showPaperPortfolio();
            break;

        case 'trades':
        case 'history':
            showPaperTrades();
            break;

        case 'reset':
            const resetAmount = parseFloat(process.argv[3]) || 10000;
            resetPaperPortfolio(resetAmount);
            break;

        default:
            console.log('Paper Trading System');
            console.log('');
            console.log('Commands:');
            console.log('  init [AMOUNT]              - Initialize paper portfolio (default: $10,000)');
            console.log('  buy <SYMBOL> <AMOUNT_USD>  - Paper buy');
            console.log('  sell <SYMBOL> <QUANTITY>   - Paper sell');
            console.log('  portfolio                  - Show current paper portfolio');
            console.log('  trades                     - Show trade history');
            console.log('  reset [AMOUNT]             - Reset portfolio');
            console.log('');
            console.log('Examples:');
            console.log('  node paper-trading.js init 10000');
            console.log('  node paper-trading.js buy BTC 1000');
            console.log('  node paper-trading.js sell BTC 0.01');
            console.log('  node paper-trading.js portfolio');
    }
}

main().catch(console.error);

