#!/usr/bin/env node

const http = require('http');
const fs = require('fs');
const path = require('path');
const { apiRequest } = require('../lib/coinbase-api.js');

const PORT = 3000;

const PAPER_PORTFOLIO_FILE = '/home/vader/.openclaw/workspace/crypto/data/paper-portfolio.json';
const PAPER_TRADES_FILE = '/home/vader/.openclaw/workspace/crypto/data/paper-trades.json';

// Load paper portfolio
function loadPaperPortfolio() {
    try {
        if (!fs.existsSync(PAPER_PORTFOLIO_FILE)) {
            return null;
        }
        return JSON.parse(fs.readFileSync(PAPER_PORTFOLIO_FILE, 'utf8'));
    } catch (err) {
        console.error('Error loading paper portfolio:', err);
        return null;
    }
}

// Load paper trades
function loadPaperTrades() {
    try {
        if (!fs.existsSync(PAPER_TRADES_FILE)) {
            return [];
        }
        return JSON.parse(fs.readFileSync(PAPER_TRADES_FILE, 'utf8'));
    } catch (err) {
        console.error('Error loading paper trades:', err);
        return [];
    }
}

// Get paper portfolio with current prices
async function getPaperPortfolioWithPrices() {
    const portfolio = loadPaperPortfolio();
    if (!portfolio) return null;

    let totalValue = portfolio.usd;

    for (const symbol of Object.keys(portfolio.holdings)) {
        try {
            const ticker = await apiRequest('GET', `/api/v3/brokerage/products/${symbol}-USD/ticker`);
            if (ticker.trades && ticker.trades.length > 0) {
                const price = parseFloat(ticker.trades[0].price);
                const qty = portfolio.holdings[symbol];
                totalValue += price * qty;
            }
        } catch (err) {
            console.error(`Failed to get price for ${symbol}:`, err.message);
        }
    }

    return {
        ...portfolio,
        totalValue
    };
}

// Get live Coinbase portfolio
async function getLivePortfolio() {
    try {
        const accounts = await apiRequest('GET', '/api/v3/brokerage/accounts');
        return accounts;
    } catch (err) {
        console.error('Failed to get live portfolio:', err);
        return { accounts: [] };
    }
}

// Get current prices
async function getCurrentPrices() {
    const symbols = ['BTC', 'ETH', 'SOL', 'ADA', 'DOT'];
    const prices = {};

    for (const symbol of symbols) {
        try {
            const ticker = await apiRequest('GET', `/api/v3/brokerage/products/${symbol}-USD/ticker`);
            if (ticker.trades && ticker.trades.length > 0) {
                prices[symbol] = parseFloat(ticker.trades[0].price);
            }
        } catch (err) {
            console.error(`Failed to get price for ${symbol}:`, err.message);
        }
    }

    return prices;
}

// Create HTTP server
const server = http.createServer(async (req, res) => {
    // CORS headers
    res.setHeader('Access-Control-Allow-Origin', '*');
    res.setHeader('Access-Control-Allow-Methods', 'GET, POST, OPTIONS');
    res.setHeader('Access-Control-Allow-Headers', 'Content-Type');

    if (req.method === 'OPTIONS') {
        res.writeHead(200);
        res.end();
        return;
    }

    // Routes
    if (req.url === '/paper/portfolio' && req.method === 'GET') {
        try {
            const portfolio = await getPaperPortfolioWithPrices();
            res.writeHead(200, { 'Content-Type': 'application/json' });
            res.end(JSON.stringify(portfolio));
        } catch (err) {
            res.writeHead(500, { 'Content-Type': 'application/json' });
            res.end(JSON.stringify({ error: err.message }));
        }
    } 
    else if (req.url === '/paper/trades' && req.method === 'GET') {
        try {
            const trades = loadPaperTrades();
            res.writeHead(200, { 'Content-Type': 'application/json' });
            res.end(JSON.stringify(trades));
        } catch (err) {
            res.writeHead(500, { 'Content-Type': 'application/json' });
            res.end(JSON.stringify({ error: err.message }));
        }
    }
    else if (req.url === '/live/portfolio' && req.method === 'GET') {
        try {
            const portfolio = await getLivePortfolio();
            res.writeHead(200, { 'Content-Type': 'application/json' });
            res.end(JSON.stringify(portfolio));
        } catch (err) {
            res.writeHead(500, { 'Content-Type': 'application/json' });
            res.end(JSON.stringify({ error: err.message }));
        }
    }
    else if (req.url === '/prices' && req.method === 'GET') {
        try {
            const prices = await getCurrentPrices();
            res.writeHead(200, { 'Content-Type': 'application/json' });
            res.end(JSON.stringify(prices));
        } catch (err) {
            res.writeHead(500, { 'Content-Type': 'application/json' });
            res.end(JSON.stringify({ error: err.message }));
        }
    }
    else {
        res.writeHead(404, { 'Content-Type': 'application/json' });
        res.end(JSON.stringify({ error: 'Not found' }));
    }
});

server.listen(PORT, () => {
    console.log(`âœ… Dashboard API server running on http://localhost:${PORT}`);
    console.log(`   Paper portfolio: http://localhost:${PORT}/paper/portfolio`);
    console.log(`   Live portfolio:  http://localhost:${PORT}/live/portfolio`);
    console.log(`   Current prices:  http://localhost:${PORT}/prices`);
    console.log(`   Trade history:   http://localhost:${PORT}/paper/trades`);
    console.log('');
    console.log(`ðŸŒ Open dashboard: /home/vader/.openclaw/workspace/crypto/dashboard/index.html`);
});

