#!/usr/bin/env node

const { apiRequest } = require('./lib/coinbase-api.js');
const fs = require('fs');

const PROFILES_FILE = '/home/vader/.openclaw/workspace/crypto/data/dca-profiles.json';
const PROFILE_TRADES_FILE = '/home/vader/.openclaw/workspace/crypto/data/profile-trades.json';

// Ensure data directory
const dataDir = '/home/vader/.openclaw/workspace/crypto/data';
if (!fs.existsSync(dataDir)) {
    fs.mkdirSync(dataDir, { recursive: true });
}

// Risk profiles (inspired by Soforge)
const RISK_PROFILES = {
    safe: {
        name: 'Safe ðŸ›¡ï¸',
        maxPositionPct: 0.10,  // 10% of capital per trade
        stopLossPct: 0.03,      // 3% stop loss
        coins: ['BTC', 'ETH'],  // Only major coins
        description: 'Conservative - Major coins only, tight stop-loss'
    },
    moderate: {
        name: 'Moderate âš–ï¸',
        maxPositionPct: 0.25,  // 25% of capital per trade
        stopLossPct: 0.07,      // 7% stop loss
        coins: ['BTC', 'ETH', 'SOL'],
        description: 'Balanced - Top coins, moderate risk'
    },
    aggressive: {
        name: 'Aggressive ðŸš€',
        maxPositionPct: 0.50,  // 50% of capital per trade
        stopLossPct: 0.15,      // 15% stop loss
        coins: ['BTC', 'ETH', 'SOL', 'ADA', 'DOT'],
        description: 'High risk - Multiple coins, larger positions'
    }
};

// Load profiles
function loadProfiles() {
    if (!fs.existsSync(PROFILES_FILE)) {
        return {};
    }
    return JSON.parse(fs.readFileSync(PROFILES_FILE, 'utf8'));
}

// Save profiles
function saveProfiles(profiles) {
    fs.writeFileSync(PROFILES_FILE, JSON.stringify(profiles, null, 2));
}

// Load profile trades
function loadProfileTrades() {
    if (!fs.existsSync(PROFILE_TRADES_FILE)) {
        return [];
    }
    return JSON.parse(fs.readFileSync(PROFILE_TRADES_FILE, 'utf8'));
}

// Log profile trade
function logProfileTrade(trade) {
    const trades = loadProfileTrades();
    trades.push({
        ...trade,
        timestamp: new Date().toISOString()
    });
    fs.writeFileSync(PROFILE_TRADES_FILE, JSON.stringify(trades, null, 2));
}

// Create profile
function createProfile(name, riskLevel, initialCapital, intervalHours) {
    const profiles = loadProfiles();

    if (!RISK_PROFILES[riskLevel]) {
        console.log(`âŒ Invalid risk level. Choose: safe, moderate, aggressive`);
        return;
    }

    const profile = {
        id: Date.now().toString(),
        name: name,
        riskLevel: riskLevel,
        riskConfig: RISK_PROFILES[riskLevel],
        initialCapital: parseFloat(initialCapital),
        currentCapital: parseFloat(initialCapital),
        intervalHours: parseInt(intervalHours),
        nextRun: new Date(Date.now() + intervalHours * 3600000).toISOString(),
        active: true,
        created: new Date().toISOString(),
        trades: 0,
        totalPnL: 0
    };

    profiles[profile.id] = profile;
    saveProfiles(profiles);

    console.log(`âœ… Profile Created: ${name}`);
    console.log(`   Risk Level: ${RISK_PROFILES[riskLevel].name}`);
    console.log(`   Initial Capital: $${initialCapital}`);
    console.log(`   Coins: ${RISK_PROFILES[riskLevel].coins.join(', ')}`);
    console.log(`   Max Position: ${(RISK_PROFILES[riskLevel].maxPositionPct * 100).toFixed(0)}%`);
    console.log(`   Stop Loss: ${(RISK_PROFILES[riskLevel].stopLossPct * 100).toFixed(0)}%`);
    console.log(`   Interval: Every ${intervalHours} hours`);
    console.log(`   Next Run: ${profile.nextRun}`);
    console.log(`   Profile ID: ${profile.id}`);
}

// List profiles
function listProfiles() {
    const profiles = loadProfiles();

    if (Object.keys(profiles).length === 0) {
        console.log('No profiles configured.');
        console.log('');
        console.log('Create one with:');
        console.log('  node multi-profile-dca.js create <NAME> <RISK_LEVEL> <CAPITAL> <INTERVAL_HOURS>');
        console.log('');
        console.log('Risk levels: safe, moderate, aggressive');
        return;
    }

    console.log('\nðŸŽ¯ DCA PROFILES:\n');

    Object.values(profiles).forEach(profile => {
        if (profile.active) {
            const riskEmoji = profile.riskLevel === 'safe' ? 'ðŸ›¡ï¸' : profile.riskLevel === 'moderate' ? 'âš–ï¸' : 'ðŸš€';
            const pnlColor = profile.totalPnL >= 0 ? '\x1b[32m' : '\x1b[31m';
            const reset = '\x1b[0m';

            console.log(`[${profile.id}] ${profile.name} ${riskEmoji}`);
            console.log(`   Risk: ${RISK_PROFILES[profile.riskLevel].name}`);
            console.log(`   Capital: $${profile.currentCapital.toFixed(2)} (started: $${profile.initialCapital})`);
            console.log(`   P&L: ${pnlColor}$${profile.totalPnL.toFixed(2)}${reset}`);
            console.log(`   Trades: ${profile.trades}`);
            console.log(`   Next Run: ${profile.nextRun}`);
            console.log(`   Coins: ${RISK_PROFILES[profile.riskLevel].coins.join(', ')}\n`);
        }
    });
}

// Remove profile
function removeProfile(id) {
    const profiles = loadProfiles();

    if (!profiles[id]) {
        console.log(`âŒ Profile ${id} not found`);
        return;
    }

    delete profiles[id];
    saveProfiles(profiles);
    console.log(`âœ… Profile ${id} removed`);
}

// Execute profile trades
async function executeProfiles(dryRun = false) {
    const profiles = loadProfiles();
    const now = new Date();

    for (const profile of Object.values(profiles)) {
        if (!profile.active) continue;

        const nextRun = new Date(profile.nextRun);

        if (now >= nextRun) {
            console.log(`\nðŸŽ¯ Executing: ${profile.name} (${RISK_PROFILES[profile.riskLevel].name})`);

            const riskConfig = RISK_PROFILES[profile.riskLevel];
            const tradeAmount = profile.currentCapital * riskConfig.maxPositionPct;

            // Pick a random coin from allowed list
            const coin = riskConfig.coins[Math.floor(Math.random() * riskConfig.coins.length)];

            try {
                // Get current price
                const ticker = await apiRequest('GET', `/api/v3/brokerage/products/${coin}-USD/ticker`);

                if (!ticker.trades || ticker.trades.length === 0) {
                    console.log(`   âŒ No price data for ${coin}`);
                    continue;
                }

                const price = parseFloat(ticker.trades[0].price);
                const quantity = (tradeAmount / price).toFixed(8);

                console.log(`   Coin: ${coin}`);
                console.log(`   Amount: $${tradeAmount.toFixed(2)} (${(riskConfig.maxPositionPct * 100).toFixed(0)}% of capital)`);
                console.log(`   Price: $${price.toFixed(2)}`);
                console.log(`   Quantity: ${quantity} ${coin}`);

                if (dryRun) {
                    console.log(`   [DRY RUN - No order placed]`);
                } else {
                    // Place market buy order
                    const orderConfig = {
                        client_order_id: `profile_${Date.now()}`,
                        product_id: `${coin}-USD`,
                        side: 'BUY',
                        order_configuration: {
                            market_market_ioc: {
                                quote_size: tradeAmount.toFixed(2)
                            }
                        }
                    };

                    const result = await apiRequest('POST', '/api/v3/brokerage/orders', orderConfig);

                    if (result.success) {
                        console.log(`   âœ… Order placed successfully`);
                        console.log(`   Order ID: ${result.order_id || result.success_response?.order_id}`);

                        // Log trade
                        logProfileTrade({
                            profileId: profile.id,
                            profileName: profile.name,
                            riskLevel: profile.riskLevel,
                            type: 'BUY',
                            coin: coin,
                            amountUSD: tradeAmount,
                            price: price,
                            quantity: quantity,
                            orderId: result.order_id || result.success_response?.order_id
                        });

                        // Update profile
                        profile.trades++;
                        profile.nextRun = new Date(Date.now() + profile.intervalHours * 3600000).toISOString();
                    } else {
                        console.log(`   âŒ Order failed: ${result.error_response?.error || 'Unknown error'}`);
                    }
                }

            } catch (err) {
                console.error(`   âŒ Error executing ${profile.name}:`, err.message);
            }
        }
    }

    saveProfiles(profiles);
}

// Show trade log
function showProfileTrades() {
    const trades = loadProfileTrades();

    if (trades.length === 0) {
        console.log('No profile trades yet.');
        return;
    }

    console.log('\nðŸ“Š Profile Trade History:\n');

    trades.slice(-20).forEach(trade => {
        console.log(`[${trade.timestamp}] ${trade.profileName} (${RISK_PROFILES[trade.riskLevel].name})`);
        console.log(`   ${trade.coin}: $${trade.amountUSD.toFixed(2)} @ $${trade.price.toFixed(2)}`);
        console.log(`   Quantity: ${trade.quantity} ${trade.coin}\n`);
    });
}

// CLI
const command = process.argv[2];

async function main() {
    switch(command) {
        case 'create':
            const name = process.argv[3];
            const riskLevel = process.argv[4];
            const capital = process.argv[5];
            const interval = process.argv[6];

            if (!name || !riskLevel || !capital || !interval) {
                console.log('Usage: multi-profile-dca.js create <NAME> <RISK_LEVEL> <CAPITAL> <INTERVAL_HOURS>');
                console.log('');
                console.log('Risk Levels:');
                console.log('  safe       - 10% positions, 3% stop-loss, BTC/ETH only');
                console.log('  moderate   - 25% positions, 7% stop-loss, BTC/ETH/SOL');
                console.log('  aggressive - 50% positions, 15% stop-loss, 5+ coins');
                console.log('');
                console.log('Example: multi-profile-dca.js create "My Safe Lane" safe 100 24');
                return;
            }

            createProfile(name, riskLevel, capital, interval);
            break;

        case 'list':
            listProfiles();
            break;

        case 'remove':
            const id = process.argv[3];
            if (!id) {
                console.log('Usage: multi-profile-dca.js remove <PROFILE_ID>');
                return;
            }
            removeProfile(id);
            break;

        case 'execute':
            const dryRun = process.argv[3] === '--dry-run';
            await executeProfiles(dryRun);
            break;

        case 'trades':
            showProfileTrades();
            break;

        default:
            console.log('Multi-Profile DCA System (Inspired by Soforge)');
            console.log('');
            console.log('Commands:');
            console.log('  create <NAME> <RISK> <CAPITAL> <HOURS>  - Create new profile');
            console.log('  list                                     - List all profiles');
            console.log('  remove <ID>                              - Remove profile');
            console.log('  execute [--dry-run]                      - Execute pending trades');
            console.log('  trades                                   - Show trade history');
            console.log('');
            console.log('Risk Levels:');
            console.log('  safe       ðŸ›¡ï¸  - Conservative (10% positions, BTC/ETH only)');
            console.log('  moderate   âš–ï¸  - Balanced (25% positions, top 3 coins)');
            console.log('  aggressive ðŸš€ - High risk (50% positions, 5+ coins)');
            console.log('');
            console.log('Examples:');
            console.log('  node multi-profile-dca.js create "Conservative Lane" safe 100 168');
            console.log('  node multi-profile-dca.js create "YOLO Lane" aggressive 50 24');
            console.log('  node multi-profile-dca.js execute --dry-run');
    }
}

main().catch(console.error);

